import {
  Container,
  TextField,
  Typography,
  Button,
  Box,
  Modal,
  Icon,
  Grid,
  Paper
} from '@mui/material';
import { useState, useEffect } from 'react';
import { Card, Row, Col } from 'reactstrap';
import { Link as RouterLink, useParams } from 'react-router-dom';
import { getSingleOrder, getSingleProduct } from '../_mocks_/repo/data';

export default function EditOrder(props) {
  const [singleOrder, setSingleOrder] = useState();
  const [singleProduct, setSingleProduct] = useState([]);
  const ids = useParams();

  const getProduct = (id) => {
    getSingleProduct(id).then((data) => {
      setSingleProduct(data.data);
    });
  };

  const init = (id) => {
    getSingleOrder(id).then((data) => {
      console.log(data.data);
      setSingleOrder(data.data);
    });
    if (singleOrder) {
      console.log(singleOrder);
      for (let i = 0; i < 2; i += 1) {
        getProduct(singleOrder.items[i].prod);
      }
    }
  };

  useEffect(() => {
    init(ids.id);
  }, [singleOrder]);

  return singleOrder ? (
    <Container>
      <Typography variant="h5" component="h5">
        Order Number #{singleOrder ? singleOrder.order_number : ''}
      </Typography>
      <Container>
        <Row>
          {singleOrder.items.map((singleProd) => (
            <Col md={3} xs={6} style={{ padding: '5px' }}>
              <Card body>
                <div>
                  <center>
                    <h5>
                      {singleProduct ? singleProduct.name : ''}*{singleProd.quantity}
                    </h5>
                  </center>
                </div>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </Container>
  ) : (
    ''
  );
}
