import { Container, TextField, Typography, Button, Box } from '@mui/material';
import { styled } from '@mui/material/styles';
import ImageList from '@mui/material/ImageList';
import ImageListItem from '@mui/material/ImageListItem';
import { useState, useEffect } from 'react';
import axios from 'axios';
// prettier-ignore

const ProductImgStyle = styled('img')({
  top: 0,
  width: '100%',
  height: '100%',
  objectFit: 'cover',
  position: 'absolute'
});

export default function AddProduct() {
  const [prodImage, setProductImage] = useState();
  const [productValues, setProductValues] = useState({
    name: '',
    description: '',
    product_detail: '',
    weight: 1,
    slug: '',
    marked_price: '',
    price: '',
    seo_meta_title: '',
    seo_meta_description: '',
    images: [
      {
        image: prodImage
      }
    ]
  });

  const formData = new FormData();
  formData.append('name', productValues.name);
  formData.append('description', productValues.description);
  formData.append('product_detail', productValues.product_detail);
  formData.append('weight', productValues.weight);
  formData.append('slug', productValues.slug);
  formData.append('marked_price', productValues.marked_price);
  formData.append('price', productValues.price);
  formData.append('seo_meta_title', productValues.seo_meta_title);
  formData.append('seo_meta_description', productValues.seo_meta_description);
  formData.append('images[0][image]', productValues.prodImage);

  const handleProductChanges = (e) => {
    setProductValues({
      ...productValues,
      [e.target.name]: e.target.value
    });
  };

  const addProduct = async (e) => {
    await axios
      .post('http://127.0.0.1:8000/api/product', productValues)
      .then((response) => {console.log(response)})
      .catch((error) => {
        console.log(error.message);
      });
    console.log(formData);
  };

  const handleImageChange = (e) => {
    setProductImage(e.target.files[0]);
  };

  return (
    <Container>
      <Typography m={2} variant="h4" gutterBottom>
        Add Product Details
      </Typography>
      <Box m={2}>
        <TextField
          label="Product Name"
          name="name"
          variant="outlined"
          value={productValues.name}
          onChange={handleProductChanges}
          fullWidth
        />
      </Box>
      <Box
        component="form"
        sx={{
          '& > :not(style)': { m: 2, width: '25ch' }
        }}
        noValidate
        autoComplete="off"
      >
        <TextField
          label="Marked Price"
          variant="outlined"
          name="marked_price"
          value={productValues.marked_price}
          onChange={handleProductChanges}
        />
        <TextField
          label="Sale Price"
          variant="outlined"
          name="price"
          value={productValues.price}
          onChange={handleProductChanges}
        />
        <TextField
          label="Weight Info"
          variant="outlined"
          name="weight"
          value={productValues.weight}
          onChange={handleProductChanges}
        />
      </Box>
      <Box m={2}>
        <TextField
          label="Short Description"
          multiline
          rows={2}
          rowsMax={2}
          name="product_detail"
          value={productValues.product_detail}
          onChange={handleProductChanges}
          fullWidth
        />
      </Box>
      <Box m={2}>
        <TextField
          label="Long Description"
          multiline
          rows={6}
          rowsMax={4}
          name="description"
          value={productValues.description}
          onChange={handleProductChanges}
          fullWidth
        />
      </Box>
      <Typography m={2} variant="h5" gutterBottom>
        SEO Section
      </Typography>
      <Box m={2}>
        <TextField
          label="Meta Title"
          variant="outlined"
          name="seo_meta_title"
          value={productValues.seo_meta_title}
          onChange={handleProductChanges}
          fullWidth
        />
      </Box>
      <Box m={2}>
        <TextField
          label="Product Slug"
          variant="outlined"
          name="slug"
          value={productValues.slug}
          onChange={handleProductChanges}
          fullWidth
        />
      </Box>
      <Box m={2}>
        <TextField
          label="Meta Description"
          multiline
          rows={2}
          rowsMax={2}
          name="seo_meta_description"
          value={productValues.seo_meta_description}
          onChange={handleProductChanges}
          fullWidth
        />
      </Box>
      <Typography m={2} variant="h5" gutterBottom>
        Product Images
      </Typography>
      <Box m={2}>
        <Button variant="outlined" component="label">
          Upload Images*
          <input type="file" name="image" onChange={handleImageChange} hidden />
        </Button>
      </Box>
      <Box m={2}>
        <Button variant="contained" component="label" onClick={addProduct} fullWidth>
          <h3>Add Product</h3>
        </Button>
      </Box>
    </Container>
  );
}
