import { Editor } from 'react-draft-wysiwyg';
import { EditorState, ContentState, convertToRaw, convertFromHTML } from 'draft-js';
import 'react-draft-wysiwyg/dist/react-draft-wysiwyg.css';
import draftToHtml from 'draftjs-to-html';
import htmlToDraft from 'html-to-draftjs';

import {
  Container,
  TextField,
  Typography,
  Button,
  Box,
  Modal,
  Icon,
  Grid,
  Stack,
  Link,
  CircularProgress
} from '@mui/material';
import { styled } from '@mui/material/styles';
import ImageList from '@mui/material/ImageList';
import ImageListItem from '@mui/material/ImageListItem';
import DeleteIcon from '@mui/icons-material/Delete';
import PropTypes from 'prop-types';
import { useState, useEffect } from 'react';
import axios from 'axios';
import * as React from 'react';
import { Card, Row, Col } from 'reactstrap';
import { Link as RouterLink, useParams } from 'react-router-dom';
import { getSingleAboutUs, getAllCard, apiUrl } from '../_mocks_/repo/data';

// prettier-ignore

export default function AboutUs(props) {

  const [editorState, setEditorState] = useState(() =>
    EditorState.createEmpty()
  );
  const [prodImage, setProductImage] = useState();
  const [displayImage, setDisplayImage] = useState();
  const [productAdded, setProductAdded] = useState(false);
  const [mediaImages, setMediaImages] = useState([]);

  const [product, setProduct] = useState([]);
  const [error, setError] = useState(false);
  const [id, setId] = useState([]);

  const ids = useParams();

  const [loader, setLoader] = useState(false);

  const [productValues, setProductValues] = useState({
    name: '',
    description: '',
    slug: '',
    seo_meta_title: '',
    seo_meta_description: '',
    featured_image:''
  });

  const [partners, setPartner] = useState([]);

  const init = () => {
    setLoader(true);
    getAllCard().then((data) => {
      if (data === undefined) {
        setLoader(true);
      } else {
        setPartner(data.data);
        setLoader(false);
      }
    });
  };
  useEffect(() => {
    init();
  }, []);

  const getSingleProduct = (id) => {
    setLoader(true);
    getAllAboutUs(id).then((data) => {
      if (data.error) {
        setError(data.error);
      } else {
        setProductValues(data.data);
        setLoader(false);
        const html = data.data.description;
        const contentBlock = htmlToDraft(html);
        if (contentBlock) {
          const contentState = ContentState.createFromBlockArray(contentBlock.contentBlocks);
          const editorState = EditorState.createWithContent(contentState);
          setEditorState(editorState)
        }
      }
    });
};
  useEffect(() => {
    getSingleProduct(ids.id);
  }, []);

  const [open, setEditOpen] = React.useState(false);
  const handleEditOpen = () => setEditOpen(true);
  const handleEditClose = () => setEditOpen(false);

  const [openMedia, setMediaOpen] = React.useState(false);
  const handleMediaOpen = () => setMediaOpen(true);
  const handleMediaClose = () => setMediaOpen(false);

  const handleImageChange = (e) => {
    setProductImage(e.target.files[0]);
    setDisplayImage(URL.createObjectURL(e.target.files[0]));
  };

  const [prodIcon, setProIcon] = useState();
  const [displayIcon, setDisplayIcon] = useState();

  const handleIconChange = (e) => {
    setProIcon(e.target.files[0]);
    setDisplayIcon(URL.createObjectURL(e.target.files[0]));
  };

  const [errortext, setErrorText] = useState();
  const [uploadError,setUploadError]= React.useState(false);

  const formData = new FormData();
  formData.append('name', productValues.name);
  formData.append('description', draftToHtml(convertToRaw(editorState.getCurrentContent())));
  formData.append('slug', productValues.slug);
  formData.append('excerpt', productValues.excerpt);
  formData.append('seo_meta_title', productValues.seo_meta_title);
  formData.append('seo_meta_description', productValues.seo_meta_description);
  if(prodIcon!=null){formData.append('service_icon', prodIcon)};
  if(prodImage!=null){formData.append('featured_image', prodImage)};

  const validateUserInput = () => {
    if(productValues.name.length === 0){
      setUploadError(true);
      setErrorText("Name is required field");
      return false;
    }
    if(editorState.getCurrentContent().getPlainText('').length === 0){
      setUploadError(true);
      setErrorText("Description is required field");
      return false;
    }
    if(productValues.slug.length === 0 || productValues.slug.length > 20 ){
      setUploadError(true);
      setErrorText("Slug is required field and must be less than 20 chars");
      return false;
    }
    return true;
  };


  const handleProductChanges = (e) => {
    setProductValues({
      ...productValues,
      [e.target.name]: e.target.value
    });
  };

  const headerFile = {
    headers:{
      'Content-Type': 'multipart/form-data',
      'Authorization': `Token ${localStorage.getItem('authToken')}`,
      }
    };

  const editProduct = async () => {
    if(validateUserInput()){
      await axios
      .patch(`${apiUrl}/api/about/1`, formData, headerFile )
      .then((response) => {
        setProductAdded(true);
        handleEditOpen();
      })
      .catch((error) => {
        console.log(error);
      });
    console.log(formData);
    }
  };

  const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 500,
    height: 200,
    overflow:'scroll',
    bgcolor: 'background.paper',
    border: '1px solid #000',
    boxShadow: 12,
    p: 4,
  };

  const PARTNERS = partners.map((_, index) => {
    const setIndex = index + 1;
    return {
      id: partners[index].id,
      title: partners[index].title,
      subtitle: partners[index].sub_title,
      description: partners[index].description
    };
  });


  const uploadInlineImageForArticle = async (file) => {
    const formda = new FormData();
    formda.append('name', 'brainstrom-image');
    formda.append('image', file);
    try {
      const data = await axios.post(
        `${apiUrl}/api/media`, 
        formda,
        headerFile
      )
      return data;
    } catch (e) {
      console.error(e);
      return null;
    }
  };

  const uploadImageCallBack = async (file) => {
    const imgData = await uploadInlineImageForArticle(file);
    console.log(imgData);
    return Promise.resolve({ data: { 
      link: imgData.data.image
    }});
  }

  return (
    <div>
      {
      productAdded?
      <Modal 
        open={open}
        onClose={handleEditClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description">
        <Box sx={style}>
          <Typography id="modal-modal-title" variant="h4" component="h2">
            Service Edited Successfully
          </Typography>
          <Typography id="modal-modal-description" sx={{ mt: 2 }}>
            <Button
              variant="contained"
              component={RouterLink}
              to="/dashboard"
              icon={<Icon name="back" size={15} color="white" />}
            >
              Go Back 
            </Button>
          </Typography>
        </Box>
      </Modal>:<Container>
        Hi
      </Container>
          }
    </div>
  );
}

SinglePartner.propTypes = {
  partner: PropTypes.object.isRequired
};

function SinglePartner({ partner }) {
  const { id, title, subtitle, description } = partner;

  return (
    <Stack direction="row" alignItems="center" spacing={2}>
      <Box sx={{ minWidth: 240 }}>
        <Link
          color="inherit"
          underline="hover"
          component={RouterLink}
          to={`/dashboard/about-card/${id}`}
        >
          <Typography variant="subtitle2" noWrap>
            {title}
          </Typography>
        </Link>
        <Typography variant="body2" sx={{ color: 'text.secondary' }} noWrap>
          {subtitle}
        </Typography>
      </Box>
    </Stack>
  );
}
