import { Container, TextField, Typography, Button, Box } from '@mui/material';
import { styled } from '@mui/material/styles';
import ImageList from '@mui/material/ImageList';
import ImageListItem from '@mui/material/ImageListItem';
import { useState, useEffect } from 'react';
import axios from 'axios';
// prettier-ignore

const ProductImgStyle = styled('img')({
  top: 0,
  width: '100%',
  height: '100%',
  objectFit: 'cover',
  position: 'absolute'
});

const addProduct = async (e) => {
  const product = {
    "name": 'Egg',
    'description': 'per pc',
    'product_detail': 'per pc',
    'weight': 1,
    'slug': 'egg',
    'marked_price': 15.0,
    'price': 15.0,
    'seo_meta_title': 'Eggs',
    'seo_meta_description': 'Egg',
    'images': [
        {
            'image': 'http://127.0.0.1:8000/media/data/media/products/eggs.png'
        }
    ]
  };
  await axios
    .post('http://127.0.0.1:8080/api/product', product)
    .then((response) => {})
    .catch((error) => {
      console.log(error.message);
    });
};

export default function AddProduct() {
  const [productName, setProductName] = useState([]);
  const [markedPrice, setMarkedPrice] = useState([]);
  const [salePrice, setSalePrice] = useState([]);
  const [shortDescription, setShortDescription] = useState([]);
  const [description, setDescription] = useState([]);
  const [weightInfo, setWeight] = useState([]);
  const [metaTitle, setMetaTitle] = useState([]);
  const [metaDescription, setMetaDescription] = useState([]);
  const [image, setImage] = useState([]);

  return (
    <Container>
      <Typography m={2} variant="h4" gutterBottom>
        Add Product Details
      </Typography>
      <Box m={2}>
        <TextField label="Product Name" variant="outlined" fullWidth />
      </Box>
      <Box
        component="form"
        sx={{
          '& > :not(style)': { m: 2, width: '25ch' }
        }}
        noValidate
        autoComplete="off"
      >
        <TextField label="Marked Price" variant="outlined" />
        <TextField label="Sale Price" variant="outlined" />
        <TextField label="Weight Info" variant="outlined" />
      </Box>
      <Box m={2}>
        <TextField label="Short Description" multiline rows={2} rowsMax={2} fullWidth />
      </Box>
      <Box m={2}>
        <TextField label="Long Description" multiline rows={6} rowsMax={4} fullWidth />
      </Box>
      <Typography m={2} variant="h5" gutterBottom>
        SEO Section
      </Typography>
      <Box m={2}>
        <TextField label="Meta Title" variant="outlined" fullWidth />
      </Box>
      <Box m={2}>
        <TextField label="Product Slug" variant="outlined" fullWidth />
      </Box>
      <Box m={2}>
        <TextField label="Meta Description" multiline rows={2} rowsMax={2} fullWidth />
      </Box>
      <Typography m={2} variant="h5" gutterBottom>
        Product Images
      </Typography>
      <Box m={2}>
        <Button variant="outlined" component="label">
          Upload Images*
          <input type="file" hidden />
        </Button>
      </Box>
      <Box m={2}>
        <Button variant="contained" component="label" fullWidth>
          <h3>Add Product</h3>
        </Button>
      </Box>
    </Container>
  );
}
