import PropTypes from 'prop-types';
import { useFormik } from 'formik';
import { Link as RouterLink } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { styled } from '@mui/material/styles';

// material
import { Container, Stack, Typography, Button, Icon, Card, Link } from '@mui/material';
import Modal from '@mui/material/Modal';
import * as React from 'react';
import Box from '@mui/material/Box';
// components
import plusFill from '@iconify/icons-eva/plus-fill';
import Page from '../components/Page';
import { ProductSort, ProductList, ProductFilterSidebar } from '../components/_dashboard/products';
//
import { getAllMedia } from '../_mocks_/repo/data';

const ProductImgStyle = styled('img')({
  top: 0,
  width: '100%',
  height: '100%',
  objectFit: 'cover',
  position: 'absolute'
});

export default function Media() {
  const [openFilter, setOpenFilter] = useState(false);
  const [visaservices, setVisaServices] = useState([]);
  const [filterName, setFilterName] = useState('');
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const init = () => {
    getAllMedia().then((data) => {
      console.log(data.data);
      setVisaServices(data.data);
    });
  };
  useEffect(() => {
    init();
  }, []);

  const MIGRATIONSERVICELIST = visaservices.map((_, index) => {
    const setIndex = index + 1;
    return {
      id: visaservices[index].id,
      cover: visaservices[index].featured_image,
      name: visaservices[index].name,
      service: 'migration-service'
    };
  });

  const handleFilterByName = (event) => {
    setFilterName(event.target.value);
  };

  return (
    <Page title="Dashboard: Products | Minimal-UI">
      <Container>
        <Stack direction="row" alignItems="center" justifyContent="space-between" mb={5}>
          <Typography variant="h4" gutterBottom>
            Available Media
          </Typography>
          <Button
            variant="contained"
            component={RouterLink}
            to="/dashboard/migration-service/add"
            icon={<Icon name="add" size={15} color="white" />}
          >
            Add Service
          </Button>
        </Stack>
        <ImageCard images={MIGRATIONSERVICELIST} />
      </Container>
    </Page>
  );
}

ImageCard.propTypes = {
  images: PropTypes.object.isRequired
};

function ImageCard({ images }) {
  const { id, name, cover, service } = images;
  return (
    <Card>
      <Box sx={{ pt: '100%', position: 'relative' }}>
        <ProductImgStyle alt={name} src={cover} />
      </Box>

      <Stack spacing={2} sx={{ p: 3 }}>
        <Link to="#" color="inherit" underline="hover" component={RouterLink}>
          <Typography variant="subtitle2" noWrap>
            {name}
          </Typography>
        </Link>
      </Stack>
    </Card>
  );
}
