import * as React from 'react';
import AdapterDateFns from '@mui/lab/AdapterDateFns';
import LocalizationProvider from '@mui/lab/LocalizationProvider';
import StaticDatePicker from '@mui/lab/StaticDatePicker';
import { Container, Grid, Typography, Button, Stack, Icon } from '@mui/material';

import ToggleButton from '@mui/material/ToggleButton';
import ToggleButtonGroup from '@mui/material/ToggleButtonGroup';
import { Link } from 'react-router-dom';

/* global gapi */
// prettier-ignore

export default function BookGoogleEvent() {
  let month = '01';

  const [value, setValue] = React.useState('');
  const [showTime, setShowTime] = React.useState(false);

  const [view, setView] = React.useState('');
  const [time, setTime] = React.useState(0);

  const handleChange = (event, nextView) => {
    setView(nextView);
    setTime(parseInt(nextView, 10));
  };

  const CLIENT_ID = '927832324416-9t64nstt3omba55i2rh1ds5lc2r3ba9m.apps.googleusercontent.com';
  const API_KEY = 'AIzaSyAyrE8iaEIFNlVsqsLvwRKFeiPf1Yh7tFU';
  const DISCOVERY_DOCS = ['https://www.googleapis.com/discovery/v1/apis/calendar/v3/rest'];
  const SCOPES = 'https://www.googleapis.com/auth/calendar';

  // required time format: 2021-12-24T19:00:00%s
  const bookevent = () => {
    dateConverter();

    // gapi.load('client:auth2', () => {
    //   gapi.client.init({
    //     apiKey: API_KEY,
    //     clientId: CLIENT_ID,
    //     discoveryDocs: DISCOVERY_DOCS,
    //     scope: SCOPES
    //   });

    //   gapi.client.load('calendar', 'v3', () => console.log('DONE'));

    //   gapi.auth2
    //     .getAuthInstance()
    //     .signIn()
    //     .then(() => {
    //       const event = {
    //         "summary": "Brainstorm Consulation",
    //         "description": "A chance to hear more about our services",
    //         "start": {
    //           "dateTime": "2022-01-10T09:00:00+05:45"
    //         },
    //         "end": {
    //           "dateTime": "2022-01-10T10:00:00+05:45"
    //         },
    //         "sendNotifications": true,
    //         "attendees": [
    //           { "email": "kanchanwalks@gmail.com" },
    //           { "email": "mailtokanchanbhatta@gmail.com" }
    //         ]
    //       };

    //       const request = gapi.client.calendar.events.insert({
    //         calendarId: 'primary',
    //         resource: event
    //       });

    //       console.log(request);

    //       request.execute((event) => {
    //         window.open(event.htmlLink);
    //       });
    //     });
    // });
  };

  const [finalDate, setFinalDate] = React.useState('');
  const [endDate, setEndFinalDate] = React.useState('');
  const dateConverter = () => {
    const cDate = value.toString();
    const myDate = cDate.split(' ');
    if (myDate[1] === 'Jan') {
      month = '01';
    }
    if (myDate[1] === 'Feb') {
      month = '02';
    }
    if (myDate[1] === 'Mar') {
      month = '03';
    }
    if (myDate[1] === 'April') {
      month = '04';
    }
    if (myDate[1] === 'May') {
      month = '05';
    }
    if (myDate[1] === 'Jun') {
      month = '06';
    }
    if (myDate[1] === 'Jul') {
      month = '07';
    }
    if (myDate[1] === 'Aug') {
      month = '08';
    }
    if (myDate[1] === 'Sep') {
      month = '09';
    }
    if (myDate[1] === 'Oct') {
      month = '10';
    }
    if (myDate[1] === 'Nov') {
      month = '11';
    }
    if (myDate[1] === 'Dec') {
      month = '12';
    }

    const theDate = `${myDate[3]}-${month}-${myDate[2]}T${time}:00:00+05:45`;
    const endingDate = `${myDate[3]}-${month}-${myDate[2]}T${time+1}:00:00+05:45`;
    setFinalDate(theDate);
    console.log(`Start: ${theDate}`);
    setEndFinalDate(endingDate);
    console.log(`Start: ${endingDate}`);

  };

  return (
    <Container>
    {time !== 0?(
      <Container>
        <Typography>
          You choose to Book your meet on <b> {value.toString().slice(4,15)} </b> from <b>{time}:00 </b> to <b>{time+1}:00</b>. Shall we proceed?
        </Typography>
        <Stack direction="row" alignItems="center" justifyContent="space-between" mb={5}>
          <Button
            variant="contained"
            onClick={bookevent}
            icon={<Icon name="add" size={15} color="white" />}
          >
            Book
          </Button>
          <Button
            variant="contained"
            component={Link}
            to="/dashboard/book"
            icon={<Icon name="add" size={15} color="white" />}
          >
            Cancel
          </Button>
        </Stack>
      </Container>
    ):(
      <Grid container spacing={2} mb={3}>
        <Grid item xs={9}>
          <LocalizationProvider dateAdapter={AdapterDateFns}>
            <StaticDatePicker
              disablePast
              orientation="landscape"
              openTo="day"
              value={value}
              onChange={(newValue) => {
                setValue(newValue);
                setShowTime(true);
              }}
            />
          </LocalizationProvider>
        </Grid>
        {showTime ? (
          <Grid item xs={2} mt={2}>
            <ToggleButtonGroup
              orientation="vertical"
              value={view}
              color="primary"
              size="large"
              exclusive
              fullWidth
              onChange={handleChange}
            >
              <ToggleButton value="10">10 AM</ToggleButton>
              <ToggleButton value="11">11 AM</ToggleButton>
              <ToggleButton value="12">12 PM</ToggleButton>
              <ToggleButton value="13">1 PM</ToggleButton>
              <ToggleButton value="14">2 PM</ToggleButton>
              <ToggleButton value="15">3 PM</ToggleButton>
              <ToggleButton value="16">4 PM</ToggleButton>
              <ToggleButton value="17">5 PM</ToggleButton>
            </ToggleButtonGroup>
          </Grid>
        ) : (
          <Container />
        )}
      </Grid>

    )}
    </Container>
  );
}
