import { filter } from 'lodash';
import {
  Card,
  Table,
  Stack,
  Avatar,
  Button,
  Checkbox,
  TableRow,
  TableBody,
  TableCell,
  Container,
  Typography,
  TableContainer,
  TablePagination,
  createSvgIcon,
  IconButton,
  Box
} from '@mui/material';
import { Icon } from '@iconify/react';
import AddIcon from '@mui/icons-material/Add';
import CreateIcon from '@mui/icons-material/Create';
import DeleteIcon from '@mui/icons-material/Delete';
import { Link as RouterLink } from 'react-router-dom';
import * as React from 'react';
import { useState, useEffect } from 'react';
import Page from '../components/Page';
import Scrollbar from '../components/Scrollbar';
import TransactionToolBar from '../components/_dashboard/user/ToolBarTransaction';
import TransactionListHead from '../components/_dashboard/user/TransactionListHead';
//

const TABLE_HEAD = [
  { id: 'name', label: 'Customer Name', alignRight: false },
  { id: 'bid', label: 'Bill Number', alignRight: false },
  { id: 'date', label: 'Date', alignRight: false },
  { id: 'amount', label: 'Amount (in Rs.)', alignRight: false }
];

function descendingComparator(a, b, orderBy) {
  if (b[orderBy] < a[orderBy]) {
    return -1;
  }
  if (b[orderBy] > a[orderBy]) {
    return 1;
  }
  return 0;
}

function getComparator(order, orderBy) {
  return order === 'desc'
    ? (a, b) => descendingComparator(a, b, orderBy)
    : (a, b) => -descendingComparator(a, b, orderBy);
}

function applySortFilter(array, comparator, query) {
  const stabilizedThis = array.map((el, index) => [el, index]);
  stabilizedThis.sort((a, b) => {
    const order = comparator(a[0], b[0]);
    if (order !== 0) return order;
    return a[1] - b[1];
  });
  if (query) {
    return filter(array, (_user) => _user.name.toLowerCase().indexOf(query.toLowerCase()) !== -1);
  }
  return stabilizedThis.map((el) => el[0]);
}

export default function SaleBill() {
  const [page, setPage] = useState(0);
  const [order, setOrder] = useState('desc');
  const [selected, setSelected] = useState([]);
  const [orderBy, setOrderBy] = useState('date');
  const [filterName, setFilterName] = useState('');
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [categories, setCategoy] = useState([]);

  const handleRequestSort = (event, property) => {
    const isAsc = orderBy === property && order === 'asc';
    setOrder(isAsc ? 'desc' : 'asc');
    setOrderBy(property);
  };

  const CATEGORYLIST = [
    { date: '2078-12-16', bid: '1', name: 'Mohan Raj', amount: '650', status: 'RECEIVED' },
    { date: '2053-02-11', bid: '9', name: 'Abhishek', amount: '890', status: 'PENDING' },
    { date: '2078-09-17', bid: '3', name: 'Kanchan Bhatta', amount: '1290', status: 'COMPLETED' },
    { date: '2063-10-23', bid: '5', name: 'Debaki', amount: '1050', status: 'REJECTED' },
    { date: '2072-02-14', bid: '7', name: 'Keshav Raj', amount: '1650', status: 'PENDING' },
    { date: '2077-08-23', bid: '2', name: 'Rudra', amount: '6500', status: 'COMPLETED' },
    { date: '2057-04-23', bid: '8', name: 'Seeta Bhatta', amount: '200', status: 'RECEIVED' },
    { date: '2042-12-17', bid: '6', name: 'Shiva', amount: '2000', status: 'PENDING' }
  ];

  const handleSelectAllClick = (event) => {
    if (event.target.checked) {
      const newSelecteds = CATEGORYLIST.map((n) => n.name);
      setSelected(newSelecteds);
      return;
    }
    setSelected([]);
  };

  const handleClick = (event, name) => {
    const selectedIndex = selected.indexOf(name);
    let newSelected = [];
    if (selectedIndex === -1) {
      newSelected = newSelected.concat(selected, name);
    } else if (selectedIndex === 0) {
      newSelected = newSelected.concat(selected.slice(1));
    } else if (selectedIndex === selected.length - 1) {
      newSelected = newSelected.concat(selected.slice(0, -1));
    } else if (selectedIndex > 0) {
      newSelected = newSelected.concat(
        selected.slice(0, selectedIndex),
        selected.slice(selectedIndex + 1)
      );
    }
    setSelected(newSelected);
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const handleFilterByName = (event) => {
    setFilterName(event.target.value);
  };

  const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - CATEGORYLIST.length) : 0;

  const filteredUsers = applySortFilter(CATEGORYLIST, getComparator(order, orderBy), filterName);

  const isUserNotFound = filteredUsers.length === 0;

  const importItems = () => {
    console.log('IMPORT ITEMS');
  };

  const exportItems = () => {
    console.log('EXPORT ITEMS');
  };

  const addItems = () => {
    console.log('Add ITEMS');
  };

  return (
    <Page title="SALES INVOICES - BIZHOPS" backgroundColor="white">
      <Container>
        <Box justifyContent="space-between" display="flex">
          <Typography variant="h1">Invoices</Typography>
          <Button
            variant="contained"
            color="primary"
            sx={{ borderRadius: 28, fontSize: 18 }}
            startIcon={<AddIcon />}
          >
            Create an invoice
          </Button>
        </Box>
      </Container>
    </Page>
  );
}
