import faker from 'faker';
import PropTypes from 'prop-types';
import { Icon } from '@iconify/react';
import { formatDistance } from 'date-fns';
import { Link as RouterLink } from 'react-router-dom';
import Grid from '@mui/material/Grid';
import axios from 'axios';
import DeleteIcon from '@mui/icons-material/Delete';
import arrowIosForwardFill from '@iconify/icons-eva/arrow-ios-forward-fill';
// material

import {
  Box,
  Stack,
  Link,
  Card,
  Button,
  Divider,
  Typography,
  CardHeader,
  Container,
  CircularProgress,
  Modal
} from '@mui/material';
import { useState, useEffect } from 'react';

import { getAllMailer, apiUrl } from '../_mocks_/repo/data';
// ----------------------------------------------------------------------

export default function Mailer() {
  const [partners, setPartner] = useState([]);
  const [loader, setLoader] = useState(false);

  const init = () => {
    setLoader(true);
    getAllMailer().then((data) => {
      if (data === undefined) {
        setLoader(true);
      } else {
        setPartner(data.data);
        setLoader(false);
      }
    });
  };
  useEffect(() => {
    init();
  }, []);

  const PARTNERS = partners.map((_, index) => {
    const setIndex = index + 1;
    return {
      id: partners[index].id,
      email: partners[index].email_id
    };
  });

  return (
    <Card>
      <Grid container spacing={2}>
        <Grid item xs={10}>
          <CardHeader title="Mail Subscriber" />
        </Grid>
        <Grid item xs={2} mt={2}>
          <Button
            variant="contained"
            component={RouterLink}
            to="/dashboard/mail/add"
            icon={<Icon name="add" size={12} color="white" />}
          >
            Add Mailer
          </Button>
        </Grid>
      </Grid>
      {loader === true ? (
        <Container m={10}>
          <CircularProgress color="secondary" />
        </Container>
      ) : (
        <Stack spacing={3} sx={{ p: 3, pr: 0 }}>
          {PARTNERS.map((news) => (
            <SinglePartner key={news.id} partner={news} />
          ))}
        </Stack>
      )}
    </Card>
  );
}

SinglePartner.propTypes = {
  partner: PropTypes.object.isRequired
};

function SinglePartner({ partner }) {
  const { id, email } = partner;

  const [done, setDone] = useState(false);

  const headerFile = {
    headers: {
      'Content-Type': 'multipart/form-data',
      Authorization: `Token ${localStorage.getItem('authToken')}`
    }
  };

  const deleteMailer = async (id) => {
    await axios
      .delete(`${apiUrl}/api/mail/${id}`, headerFile)
      .then((response) => {
        setDone(true);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 500,
    height: 200,
    overflow:'scroll',
    bgcolor: 'background.paper',
    border: '1px solid #000',
    boxShadow: 12,
    p: 4,
  };

  return (
    <div>
      {done ? (
        <Modal
          open={open}
          onClose={handleEditClose}
          aria-labelledby="modal-modal-title"
          aria-describedby="modal-modal-description"
        >
          <Box sx={style}>
            <Typography id="modal-modal-title" variant="h4" component="h2">
              Service Edited Successfully
            </Typography>
            <Typography id="modal-modal-description" sx={{ mt: 2 }}>
              <Button
                variant="contained"
                component={RouterLink}
                to="/dashboard/mail"
                icon={<Icon name="back" size={15} color="white" />}
              >
                Go Back
              </Button>
            </Typography>
          </Box>
        </Modal>
      ) : (
        <Stack direction="row" alignItems="center" spacing={2}>
          <Box sx={{ minWidth: 240 }}>
            <Link
              color="inherit"
              underline="hover"
              component={RouterLink}
              to={`/dashboard/mail/${id}`}
            >
              <Typography variant="subtitle2" noWrap>
                {email}
              </Typography>
            </Link>
          </Box>
          <Box>
            <DeleteIcon onClick={deleteMailer(id)} style={{ color: 'red' }} />
          </Box>
        </Stack>
      )}
    </div>
  );
}
