import faker from 'faker';
import PropTypes from 'prop-types';
import { Icon } from '@iconify/react';
import { formatDistance } from 'date-fns';
import { Link as RouterLink } from 'react-router-dom';
import Grid from '@mui/material/Grid';
import arrowIosForwardFill from '@iconify/icons-eva/arrow-ios-forward-fill';
// material

import { Box, Stack, Link, Card, Button, Divider, Typography, CardHeader } from '@mui/material';
import { useState, useEffect } from 'react';

import { getAllPartners } from '../_mocks_/repo/data';
// ----------------------------------------------------------------------

export default function Partner() {
  const [partners, setPartner] = useState([]);

  const init = () => {
    getAllPartners().then((data) => {
      setPartner(data.data);
    });
  };
  useEffect(() => {
    init();
  }, []);

  const PARTNERS = partners.map((_, index) => {
    const setIndex = index + 1;
    return {
      id: partners[index].id,
      image: partners[index].featured_image,
      title: partners[index].name,
      category: partners[index].category
    };
  });

  return (
    <Card>
      <Grid container spacing={2}>
        <Grid item xs={8}>
          <CardHeader title="News Update" />
        </Grid>
        <Grid item xs={4} mt={2}>
          <Button
            variant="contained"
            component={RouterLink}
            to="/dashboard/blog/add"
            icon={<Icon name="add" size={12} color="white" />}
          >
            Add Blog
          </Button>
        </Grid>
      </Grid>

      <Stack spacing={3} sx={{ p: 3, pr: 0 }}>
        {PARTNERS.map((news) => (
          <SinglePartner key={news.name} news={news} />
        ))}
      </Stack>
    </Card>
  );
}

SinglePartner.propTypes = {
  partner: PropTypes.object.isRequired
};

function SinglePartner({ partner }) {
  const { id, image, title, category } = partner;

  return (
    <Stack direction="row" alignItems="center" spacing={2}>
      <Box
        component="img"
        alt={title}
        src={image}
        sx={{ width: 80, height: 60, borderRadius: 1.0 }}
      />
      <Box sx={{ minWidth: 240 }}>
        <Link color="inherit" underline="hover" component={RouterLink} to={`/dashboard/blog/${id}`}>
          <Typography variant="subtitle2" noWrap>
            {title}
          </Typography>
        </Link>
        <Typography variant="body2" sx={{ color: 'text.secondary' }} noWrap>
          {category}
        </Typography>
      </Box>
    </Stack>
  );
}
