import PropTypes from 'prop-types';
import { useFormik } from 'formik';
import { Link as RouterLink } from 'react-router-dom';
import { useState, useEffect } from 'react';
import { styled } from '@mui/material/styles';

// material
import { Container, Stack, Typography, Button, Icon, Card, Link, Grid } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';

import Modal from '@mui/material/Modal';
import * as React from 'react';
import axios from 'axios';
import Box from '@mui/material/Box';
// components
import plusFill from '@iconify/icons-eva/plus-fill';
import Page from '../components/Page';
import { ProductSort, ProductList, ProductFilterSidebar } from '../components/_dashboard/products';

//
import { getAllMedia } from '../_mocks_/repo/data';

const ProductImgStyle = styled('img')({
  top: 0,
  width: '100%',
  height: '100%',
  objectFit: 'cover',
  position: 'absolute'
});

export default function Media() {
  const [openFilter, setOpenFilter] = useState(false);
  const [visaservices, setVisaServices] = useState([]);
  const [filterName, setFilterName] = useState('');
  const [open, setOpen] = React.useState(false);

  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const init = () => {
    getAllMedia().then((data) => {
      console.log(data.data);
      setVisaServices(data.data);
    });
  };
  useEffect(() => {
    init();
  }, []);

  const MIGRATIONSERVICELIST = visaservices.map((_, index) => {
    const setIndex = index + 1;
    return {
      id: visaservices[index].id,
      image: visaservices[index].image,
      name: visaservices[index].name
    };
  });

  const handleFilterByName = (event) => {
    setFilterName(event.target.value);
  };

  return (
    <Page title="Dashboard: Products | Minimal-UI">
      <Container>
        <Stack direction="row" alignItems="center" justifyContent="space-between" mb={5}>
          <Typography variant="h4" gutterBottom>
            Available Media
          </Typography>
          <Button
            variant="contained"
            component={RouterLink}
            to="/dashboard/media/add"
            icon={<Icon name="add" size={15} color="white" />}
          >
            Add Media
          </Button>
        </Stack>
        <Grid container spacing={1}>
          {MIGRATIONSERVICELIST.map((service) => (
            <Grid item key={service.id}>
              <ImageCard images={service} func = {init()}/>
            </Grid>
          ))}
        </Grid>
      </Container>
    </Page>
  );
}

ImageCard.propTypes = {
  images: PropTypes.object.isRequired
};

function ImageCard({ images, func }) {
  const headerFile = {
    headers: {
      'Content-Type': 'multipart/form-data',
      Authorization: 'Token b4e1b0ee69c8f174e1f897f2af70567fa31b4760'
    }
  };

  const deleteProduct = async (id) => {
    await axios
      .delete(`http://127.0.0.1:8000/api/partner/${id}`, headerFile)
      .then((response) => {
        func();
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const { id, name, image } = images;
  return (
    <Card>
      <Box sx={{ pt: '100%', position: 'relative' }}>
        <ProductImgStyle alt={name} src={image} />
      </Box>

      <Stack spacing={2} sx={{ p: 3 }}>
        <Link to="#" color="inherit" underline="hover" component={RouterLink}>
          <Typography variant="subtitle2" noWrap>
            {name}
          </Typography>
          <Button
            variant="outlined"
            component="label"
            onClick={deleteProduct}
            color="error"
            startIcon={<DeleteIcon />}
          >
            <h3>Delete</h3>
          </Button>
        </Link>
      </Stack>
    </Card>
  );
}
