import {
  Container,
  TextField,
  Typography,
  Button,
  Box,
  Modal,
  Icon,
  Grid,
  Paper
} from '@mui/material';
import { useState, useEffect } from 'react';
import { Card, Row, Col } from 'reactstrap';
import { Link as RouterLink, useParams } from 'react-router-dom';
import { getSingleOrder, getSingleProduct } from '../_mocks_/repo/data';
import OrderItems from './OrderItems';

export default function EditOrder(props) {
  const [singleOrder, setSingleOrder] = useState();
  const [singleProduct, setSingleProduct] = useState([]);
  const ids = useParams();

  const init = async (id) => {
    await getSingleOrder(id).then((data) => {
      setSingleOrder(data.data);
    });
    if (singleOrder) {
      const item = [];
      for (let i = 0; i < singleOrder.items.length; i = +1) {
        getSingleProduct(singleOrder.items[i].prod).then((data) => {
          item.push(data.data);
          console.log(item);
        });
      }
    }
  };
  useEffect(() => {
    init(ids.id);
  }, []);

  return singleOrder ? (
    <Container>
      <Typography variant="h5" component="h5">
        Order Number #{singleOrder ? singleOrder.order_number : ''}
      </Typography>
      <Container>
        <Row>
          <table>
            <thead>
              <tr>
                <th scope="col">Items</th>
                <th scope="col">Qty</th>
              </tr>
            </thead>
            {singleOrder.items.map((singleProd) => (
              <OrderItems id={singleProd.prod}/>
            ))}
          </table>
        </Row>
      </Container>
    </Container>
  ) : (
    ''
  );
}
