import {
  TextField,
  Typography,
  Button,
  Box,
  Modal,
  Icon,
  Grid,
  Paper
} from '@mui/material';
import { useState, useEffect } from 'react';
import { Card, Container, Row, Col } from 'reactstrap';
import { Link as RouterLink, useParams } from 'react-router-dom';
import { getSingleOrder, getSingleProduct } from '../_mocks_/repo/data';

export default function EditOrder(props) {
  const [singleOrder, setSingleOrder] = useState();
  const [singleProduct, setSingleProduct] = useState();
  const ids = useParams();

  const getProduct = (id) => {
    getSingleProduct(id).the((data) => {
      setSingleProduct(data.data);
    });
  };

  useEffect(() => {
    const init = (id) => {
      getSingleOrder(id).then((data) => {
        console.log(data.data);
        setSingleOrder(data.data);
      });
    };
    console.log(ids.id);
    init(ids.id);
  }, []);

  return (
    <Container>
      <Typography variant="h5" component="h5">
        Order Number #{singleOrder ? singleOrder.order_number : ''}
      </Typography>
      <Container>
          <Row>
            {products.map((product) => {
            return (
              <Col md={3} xs={6} style={{padding: '5px'}}>
                <Card body> 
                  <img className="responsive-img" src={product.images[0].image} height="150px"/>
                  <div><center><h5>{product.name}</h5></center></div>
                </Card>
              </Col>
              );
            })}
          </Row>
      </Container>
    </Container>
  );
}
