import {
  Container,
  TextField,
  Typography,
  Button,
  Box,
  Modal,
  Icon,
  Grid,
  Paper
} from '@mui/material';
import { styled } from '@mui/material/styles';
import ImageList from '@mui/material/ImageList';
import ImageListItem from '@mui/material/ImageListItem';
import DeleteIcon from '@mui/icons-material/Delete';
import PropTypes from 'prop-types';
import { useState, useEffect } from 'react';
import axios from 'axios';
import * as React from 'react';
import { Card, Row, Col } from 'reactstrap';
import { Link as RouterLink, useParams } from 'react-router-dom';
import { getAllCounter, apiUrl } from '../_mocks_/repo/data';

// prettier-ignore

export default function AddCounter(props) {


        const [prodImage, setProductImage] = useState();
        const [displayImage, setDisplayImage] = useState();
        const [productAdded, setProductAdded] = useState(false);
        const [mediaImages, setMediaImages] = useState([]);

        const [product, setProduct] = useState([]);
        const [error, setError] = useState(false);
        const [id, setId] = useState([]);

        const ids = useParams();

        const [productValues, setProductValues] = useState({
          id: '',
          counterTitle: '',
          icon: '',
          count: ''
        });

        const [errortext, setErrorText] = useState();
    const [uploadError,setUploadError]= React.useState(false);

    const validateUserInput = () => {
      if(productValues.counterTitle.length === 0){
        setUploadError(true);
        setErrorText("Name is required field");
        return false;
      }
      if(productValues.count.length === 0){
        setUploadError(true);
        setErrorText("URL is required field");
        return false;
      }
      if(prodImage == null){
        setUploadError(true);
        setErrorText("Image is required field");
        return false;
      }
      return true;
    };

        const [open, setEditOpen] = React.useState(false);
        const handleEditOpen = () => setEditOpen(true);
        const handleEditClose = () => setEditOpen(false);

        const [openMedia, setMediaOpen] = React.useState(false);
        const handleMediaOpen = () => setMediaOpen(true);
        const handleMediaClose = () => setMediaOpen(false);

        const handleImageChange = (e) => {
          setProductImage(e.target.files[0]);
          setDisplayImage(URL.createObjectURL(e.target.files[0]));
        };

        const formData = new FormData();
        formData.append('counter_title', productValues.counterTitle);
        formData.append('count', productValues.count);
        if(prodImage!=null){formData.append('icon', prodImage)};


        const handleProductChanges = (e) => {
          setProductValues({
            ...productValues,
            [e.target.name]: e.target.value
          });
        };

        const headerFile = {
          headers:{
            'Content-Type': 'multipart/form-data',
            'Authorization': `Token ${localStorage.getItem('authToken')}`,
            }
          };

        const addPartner = async () => {
          if(validateUserInput()){
            await axios
            .post(`${apiUrl}/api/counter`, formData, headerFile )
            .then((response) => {
              setProductAdded(true);
              handleEditOpen();
            })
            .catch((error) => {
              setUploadError(true);
              setErrorText("Something went wrong! Please try again..")
            });
          }
        };

        const style = {
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          width: 500,
          height: 200,
          overflow:'scroll',
          bgcolor: 'background.paper',
          border: '1px solid #000',
          boxShadow: 12,
          p: 4,
        };

        return (
          <div>
            {
            productAdded?
            <Modal 
              open={open}
              onClose={handleEditClose}
              aria-labelledby="modal-modal-title"
              aria-describedby="modal-modal-description">
              <Box sx={style}>
                <Typography id="modal-modal-title" variant="h4" component="h2">
                  Service Edited Successfully
                </Typography>
                <Typography id="modal-modal-description" sx={{ mt: 2 }}>
                  <Button
                    variant="contained"
                    component={RouterLink}
                    to="/dashboard/counter"
                    icon={<Icon name="back" size={15} color="white" />}
                  >
                    Go Back 
                  </Button>
                </Typography>
              </Box>
            </Modal>:
                <Container>
                <Typography m={2} variant="h4" gutterBottom> 
                  Add Counter
                </Typography>
                <Box m={2}>
                  <TextField
                    label="Counter Title"
                    name="counterTitle"
                    variant="outlined"
                    value={productValues.counterTitle}
                    onChange={handleProductChanges}
                    fullWidth
                  />
                </Box>
                <Box m={2}>
                  <TextField
                    label="Count"
                    variant="outlined"
                    name="count"
                    value={productValues.count}
                    onChange={handleProductChanges}
                    fullWidth
                  />
                </Box>
                <Box m={2}>
                  {displayImage==null?<img src={ productValues.icon } alt="img" height="100px"/>:<img src={ displayImage } alt="img" height="100px"/>}
                  <Button variant="outlined" component="label">
                    Upload Icon*
                    <input type="file" name="image" onChange={handleImageChange} hidden />
                  </Button>
                </Box>
                <Box m={2}>
                {
                  uploadError?(<Typography variant="p" color="red">{errortext}</Typography>):(<Container/>)
                }
                </Box>
                <Box m={2}>
                  <Button variant="contained" component="label" onClick={addPartner} fullWidth>
                    <h3>Add Counter</h3>
                  </Button>
                </Box>
              </Container>
              }
          </div>
        );
      }

AddCounter.propTypes = {
  location: PropTypes.object
};
