import {
  Container,
  TextField,
  Typography,
  Button,
  Box,
  Modal,
  Icon,
  Grid,
  Paper,
  TableRow
} from '@mui/material';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import { useState, useEffect } from 'react';
import { Card, Row, Col } from 'reactstrap';
import { Link as RouterLink, useParams } from 'react-router-dom';
import { getSingleOrder, getSingleProduct } from '../_mocks_/repo/data';
import OrderItems from './OrderItems';

export default function EditOrder(props) {
  const [singleOrder, setSingleOrder] = useState();
  const [singleProduct, setSingleProduct] = useState([]);
  const ids = useParams();

  const init = async (id) => {
    await getSingleOrder(id).then((data) => {
      setSingleOrder(data.data);
    });
  };
  useEffect(() => {
    init(ids.id);
  }, []);

  return singleOrder ? (
    <Container>
      <Typography variant="h4" component="h4">
        Order Number #{singleOrder ? singleOrder.order_number : ''}
      </Typography>
      <Container>
        <Row>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell scope="col">Items</TableCell>
                <TableCell scope="col">Qty</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {singleOrder.items.map((singleProd) => (
                <TableRow>
                  <TableCell>
                    <OrderItems id={singleProd.prod} />
                  </TableCell>
                  <TableCell style={{ textAlign: 'left', marginLeft: 20 }}>
                    {singleProd.quantity}
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </Row>
      </Container>
      <Typography variant="h6" component="h6">
        Delivery Details
      </Typography>
      <Table>
        <TableBody>
          <TableRow>
            <TableCell>Delivery Date</TableCell>
            <TableCell style={{ textAlign: 'left', marginLeft: 20 }}>
              {singleOrder.delivery_date.slice(0, 12)}
            </TableCell>
          </TableRow>
          <TableRow>
            <TableCell>Delivery Address</TableCell>
            <TableCell style={{ textAlign: 'left', marginLeft: 20 }}>
              {singleOrder.order_address[0].address_1}, {singleOrder.order_address[0].address_2}
            </TableCell>
          </TableRow>
        </TableBody>
      </Table>
    </Container>
  ) : (
    ''
  );
}
