import { filter } from 'lodash';
import {
  Card,
  Table,
  Stack,
  Avatar,
  Button,
  Checkbox,
  TableRow,
  TableBody,
  TableCell,
  Container,
  Typography,
  TableContainer,
  TablePagination,
  createSvgIcon,
  IconButton,
  Box,
  Paper,
  Grid,
  Tooltip
} from '@mui/material';
import { Icon } from '@iconify/react';
import AddIcon from '@mui/icons-material/Add';
import CreateIcon from '@mui/icons-material/Create';
import DeleteIcon from '@mui/icons-material/Delete';
import { Link as RouterLink } from 'react-router-dom';
import * as React from 'react';
import { useState, useEffect } from 'react';
import Page from '../components/Page';
import Scrollbar from '../components/Scrollbar';
import TransactionToolBar from '../components/_dashboard/user/ToolBarTransaction';
import TransactionListHead from '../components/_dashboard/user/TransactionListHead';
//

const TABLE_HEAD = [
  { id: 'name', label: 'Product Name', alignRight: false },
  { id: 'count', label: 'Stock Count', alignRight: false },
  { id: 'tnxdate', label: 'Last Stock In ', alignRight: false },
  { id: 'action', label: 'Action', alignRight: false }
];

function descendingComparator(a, b, orderBy) {
  if (b[orderBy] < a[orderBy]) {
    return -1;
  }
  if (b[orderBy] > a[orderBy]) {
    return 1;
  }
  return 0;
}

function getComparator(order, orderBy) {
  return order === 'desc'
    ? (a, b) => descendingComparator(a, b, orderBy)
    : (a, b) => -descendingComparator(a, b, orderBy);
}

function applySortFilter(array, comparator, query) {
  const stabilizedThis = array.map((el, index) => [el, index]);
  stabilizedThis.sort((a, b) => {
    const order = comparator(a[0], b[0]);
    if (order !== 0) return order;
    return a[1] - b[1];
  });
  if (query) {
    return filter(array, (_user) => _user.name.toLowerCase().indexOf(query.toLowerCase()) !== -1);
  }
  return stabilizedThis.map((el) => el[0]);
}

export default function Inventory() {
  const [page, setPage] = useState(0);
  const [order, setOrder] = useState('desc');
  const [selected, setSelected] = useState([]);
  const [orderBy, setOrderBy] = useState('date');
  const [filterName, setFilterName] = useState('');
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [categories, setCategoy] = useState([]);

  const handleRequestSort = (event, property) => {
    const isAsc = orderBy === property && order === 'asc';
    setOrder(isAsc ? 'desc' : 'asc');
    setOrderBy(property);
  };

  const CATEGORYLIST = [
    { duedate: '2078-12-16', txndate: '2078-12-16', bid: '1', name: 'Mohan Raj', count: '650' },
    { duedate: '2053-02-11', txndate: '2053-02-11', bid: '9', name: 'Abhishek', count: '890' },
    {
      duedate: '2078-09-17',
      txndate: '2078-09-17',
      bid: '3',
      name: 'Kanchan Bhatta',
      count: '1290'
    }
  ];

  const handleSelectAllClick = (event) => {
    if (event.target.checked) {
      const newSelecteds = CATEGORYLIST.map((n) => n.name);
      setSelected(newSelecteds);
      return;
    }
    setSelected([]);
  };

  const handleClick = (event, name) => {
    const selectedIndex = selected.indexOf(name);
    let newSelected = [];
    if (selectedIndex === -1) {
      newSelected = newSelected.concat(selected, name);
    } else if (selectedIndex === 0) {
      newSelected = newSelected.concat(selected.slice(1));
    } else if (selectedIndex === selected.length - 1) {
      newSelected = newSelected.concat(selected.slice(0, -1));
    } else if (selectedIndex > 0) {
      newSelected = newSelected.concat(
        selected.slice(0, selectedIndex),
        selected.slice(selectedIndex + 1)
      );
    }
    setSelected(newSelected);
  };

  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const handleFilterByName = (event) => {
    setFilterName(event.target.value);
  };

  const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - CATEGORYLIST.length) : 0;

  const filteredUsers = applySortFilter(CATEGORYLIST, getComparator(order, orderBy), filterName);

  const isUserNotFound = filteredUsers.length === 0;

  const importItems = () => {
    console.log('IMPORT ITEMS');
  };

  const exportItems = () => {
    console.log('EXPORT ITEMS');
  };

  const addItems = () => {
    console.log('Add ITEMS');
  };

  return (
    <Page title="INVENTORY - BIZHOPS" backgroundColor="white">
      <Container>
        <Box justifyContent="space-between" display="flex" mb={3}>
          <Typography variant="h1">Inventory Stats</Typography>
        </Box>
        <Paper
          style={{
            padding: '26px 20px',
            borderWidth: `2px !important`,
            borderColor: `silver`
          }}
          sx={{ mb: 6 }}
          variant="outlined"
        >
          <Box justifyContent="space-between" display="flex">
            <Box>
              <Typography variant="subheading" mb={3} sx={{ fontSize: 12 }}>
                In Stock
              </Typography>
              <Typography variant="subtitle1" sx={{ fontSize: 22 }}>
                200
              </Typography>
            </Box>
            <Box>
              <Typography variant="subheading" mb={3} sx={{ fontSize: 12 }}>
                Warning
              </Typography>
              <Typography variant="subtitle1" sx={{ fontSize: 22 }}>
                13
              </Typography>
            </Box>
            <Box>
              <Typography variant="subheading" mb={3} sx={{ fontSize: 12 }}>
                Out Of Stock
              </Typography>
              <Typography variant="subtitle1" sx={{ fontSize: 22 }}>
                7
              </Typography>
            </Box>
          </Box>
        </Paper>
        <TransactionToolBar
          numSelected={selected.length}
          filterName={filterName}
          onFilterName={handleFilterByName}
          onImportClick={importItems}
          onExportClick={exportItems}
          onAddClick={addItems}
        />
        <Scrollbar>
          <TableContainer sx={{ minWidth: 700 }}>
            <Table sx={{ backgroundColor: 'white', mt: 2 }}>
              <TransactionListHead
                order={order}
                orderBy={orderBy}
                headLabel={TABLE_HEAD}
                rowCount={CATEGORYLIST.length}
                numSelected={selected.length}
                onRequestSort={handleRequestSort}
                onSelectAllClick={handleSelectAllClick}
              />
              <TableBody>
                {filteredUsers
                  .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                  .map((row) => {
                    const { id, duedate, txndate, bid, name, count } = row;
                    const isItemSelected = selected.indexOf(name) !== -1;

                    return (
                      <TableRow hover key={id} tabIndex={-1}>
                        <TableCell align="left">{name}</TableCell>
                        <TableCell align="left">{count}</TableCell>
                        <TableCell align="left">{txndate}</TableCell>
                        <TableCell align="left">
                          <IconButton color="secondary" aria-label="Edit/Delete" component="span">
                            <CreateIcon />
                          </IconButton>
                          <IconButton color="primary" aria-label="Delete" component="span">
                            <DeleteIcon />
                          </IconButton>
                        </TableCell>
                      </TableRow>
                    );
                  })}
                {emptyRows > 0 && (
                  <TableRow style={{ height: 53 * emptyRows }}>
                    <TableCell colSpan={6} />
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </TableContainer>
        </Scrollbar>

        <TablePagination
          rowsPerPageOptions={[10, 20, 30]}
          component="div"
          count={CATEGORYLIST.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onPageChange={handleChangePage}
          onRowsPerPageChange={handleChangeRowsPerPage}
        />
      </Container>
    </Page>
  );
}
